function load_gem_set(gem_number) {
  setHTMLElemStyle('display', 'block', 'local_preloader_screen', true);
  for (var i_index in gem_loaded) {
    i = gem_loaded[i_index];
    if (i == true) {
      unloadScene(['gem_',i_count,'.gltf'].join(''));
      gem_loaded[i_count] = false;
    }
    i_count = (typeof i_count == 'number' ? i_count : 0) + 1;
  }
  appendScene(['gem_',gem_number,'.gltf.xz'].join(''), ['gem_',gem_number,'.gltf.xz'].join(''), false, false, function() {
    buttons_select_remove();
    gem_type = gem_number;
    html_buttons_acess_update();
    ring_update();
    gem_loaded[gem_number] = true;
    setHTMLElemStyle('display', 'none', 'local_preloader_screen', true);
    metall_material_update();
    gem_color_update();
    i_count = 0;
    update_engraving();
  }, function() {
    setHTMLElemAttribute('innerHTML', String(Math.round(_pGlob.percentage)) + '%', 'local_preloader_percentage', true);
  });
}


